                  <Button
                    onClick={() => {
                      setModalComestiblesOpen(true);
                      setProductoEditar(null);
                    }}
                    className="bg-green-700 text-white font-bold px-5 py-2 rounded-lg hover:bg-green-800 transition"
                  >
                    <ClipboardPlus className="w-5 h-5 text-white" />
                    Registrar Entrada
