import { jwtDecode } from "jwt-decode";

export default function AdminDashboard() {
  const location = useLocation();
  const {
    users,
    loading,
    error,
    currentUser,
    modalOpen,
    setModalOpen,
    newUser,
    setNewUser,
    creating,
    createNewUser,
    updateUserData,
    deleteUserData,
    showEmailUpdatedModal,
    setShowEmailUpdatedModal,
  } = useUsers();

  const [logoutModalOpen, setLogoutModalOpen] = useState(false);
  const [deleteUserModal, setDeleteUserModal] = useState({ open: false, userId: null });
  const [tokenExpiring, setTokenExpiring] = useState(false);

  function handleLogoutConfirm() {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  }

  function handleEmailUpdatedConfirm() {
    setShowEmailUpdatedModal(false);
    handleLogoutConfirm();
  }

  function handleDeleteUserConfirm() {
    deleteUserData(deleteUserModal.userId);
    setDeleteUserModal({ open: false, userId: null });
  }

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const decoded = jwtDecode(token);
      const currentTime = Math.floor(Date.now() / 1000);
      const timeLeft = decoded.exp - currentTime;

      if (timeLeft <= 180) setTokenExpiring(true);

      const timer = setTimeout(() => setTokenExpiring(true), (timeLeft - 180) * 1000);
      return () => clearTimeout(timer);
    } catch (err) {
      console.error("Token inválido:", err);
      handleLogoutConfirm();
    }
  }, []);

  let sidebarActive = location.pathname;

  return (
    <div className="bg-neutral-100 min-h-screen font-sans flex">
      {/* Sidebar */}
      <Sidebar onLogout={() => setLogoutModalOpen(true)} active={sidebarActive} />

      {/* Contenido principal */}
      <main className="ml-72 p-8 w-full space-y-6 animate-fade-in">
        {/* Encabezado principal */}
        <div className="bg-white rounded-2xl shadow-xl p-6 border border-neutral-200 hover:shadow-2xl transition-all duration-500">
          <h1 className="text-4xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-rose-700 via-rose-500 to-amber-500 tracking-tight">
            👋 Bienvenido {currentUser.username || "Administrador"}
          </h1>
          <p className="text-gray-500 mt-2 text-lg">
            Administra tu personal y la información de tu empresa en un solo lugar.
          </p>
        </div>

        {/* Info + Imagen */}
        <div className="flex flex-col lg:flex-row gap-6 items-stretch">
          {/* InfoEmpresa ocupa la mitad */}
          <div className="flex-1">
            <InfoEmpresa />
          </div>

          {/* Imagen ocupa la otra mitad pero más baja */}
          <div className="flex-1 relative rounded-2xl overflow-hidden shadow-2xl 
                          hover:scale-[1.02] transition-transform duration-200
                          max-h-[328px]"> {/* ⬅️ altura máxima reducida */}
            <img
              src="/images/info.jpeg"
              alt="Gimnasio moderno"
              className="w-full h-full object-cover"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/70 to-transparent"></div>
          </div>
        </div>


        {/* Personal a cargo */}
        <div className="rounded-2xl shadow-xl border border-neutral-200 overflow-hidden transition-all">
          {/* Encabezado degradado */}
          <div
            className="bg-gradient-to-r from-red-900 via-black to-red-950 
           border-b border-red-700 
           shadow-[0_0_30px_#ff1a1acc] 
           p-5 flex justify-between items-center"

          >
            <h2 className="text-white text-2xl font-semibold tracking-wide">
              Personal a Cargo
            </h2>

            {currentUser.rol === "admin" && (
              <button
                onClick={() => setModalOpen(true)}
                className="flex items-center space-x-2 px-4 py-2 rounded-xl 
                           bg-gradient-to-r from-red-700 to-red-900
                           hover:from-red-600 hover:to-red-800
                           transition-all duration-300
                           text-white font-medium shadow-md hover:shadow-lg"
              >
                <UserPlus className="w-5 h-5" /> {/* ✅ Ícono agregado aquí */}
                <span>Crear Trabajador</span>
              </button>
            )}
          </div>

          {/* Contenido */}
          <div className="bg-white p-6">
            <UserList
              users={users}
              loading={loading}
              error={error}
              currentUser={currentUser}
              onEdit={updateUserData}
              onDelete={(id) => setDeleteUserModal({ open: true, userId: id })}
            />
          </div>
        </div>
      </main>

      {/* Modales (igual que antes) */}
      <CreateUserModal
        modalOpen={modalOpen}
        setModalOpen={setModalOpen}
        newUser={newUser}
        setNewUser={setNewUser}
        creating={creating}
        onCreate={createNewUser}
      />

      <Modal isOpen={logoutModalOpen} onClose={() => setLogoutModalOpen(false)} title="¿Cerrar sesión?">
        <p className="text-gray-700 text-center mb-6">
          ¿Estás seguro que deseas salir de tu cuenta?
        </p>
        <div className="flex justify-center space-x-4">
          <button
            onClick={() => setLogoutModalOpen(false)}
            className="px-5 py-2 rounded-lg bg-gray-200 hover:bg-gray-300 text-gray-800 transition"
          >
            Cancelar
          </button>
          <button
            onClick={handleLogoutConfirm}
            className="px-5 py-2 rounded-lg bg-rose-600 hover:bg-rose-500 text-white transition shadow-lg"
          >
            Cerrar Sesión
          </button>
        </div>
      </Modal>

      <Modal
        isOpen={deleteUserModal.open}
        onClose={() => setDeleteUserModal({ open: false, userId: null })}
        title="¿Eliminar usuario?"
      >
        <p className="text-gray-700 text-center mb-6">
          ¿Seguro que deseas eliminar este usuario?
        </p>
        <div className="flex justify-center space-x-4">
          <button
            onClick={() => setDeleteUserModal({ open: false, userId: null })}
            className="px-5 py-2 rounded-lg bg-gray-200 hover:bg-gray-300 text-gray-800 transition"
          >
            Cancelar
          </button>
          <button
            onClick={handleDeleteUserConfirm}
            className="px-5 py-2 rounded-lg bg-rose-600 hover:bg-rose-500 text-white transition shadow-lg"
          >
            Eliminar
          </button>
        </div>
      </Modal>

      <Modal isOpen={showEmailUpdatedModal} onClose={handleEmailUpdatedConfirm} title="Correo actualizado">
        <p className="text-gray-700 text-center mb-6">
          Tu correo fue actualizado. Debes volver a iniciar sesión.
        </p>
        <div className="flex justify-center">
          <button
            onClick={handleEmailUpdatedConfirm}
            className="px-6 py-2 rounded-lg bg-rose-600 hover:bg-rose-500 text-white transition shadow-lg"
          >
            Volver a iniciar sesión
          </button>
        </div>
      </Modal>

      <Modal isOpen={tokenExpiring} onClose={handleLogoutConfirm} title="Sesión a punto de expirar">
        <p className="text-gray-700 text-center mb-4">
          Tu sesión está a punto de expirar. Por seguridad, debes iniciar sesión nuevamente.
        </p>
        <div className="flex justify-center">
          <button
            onClick={handleLogoutConfirm}
            className="px-6 py-2 rounded-lg bg-rose-600 hover:bg-rose-500 text-white transition shadow-lg"
          >
            Iniciar Sesión Nuevamente
          </button>
        </div>
      </Modal>
    </div>
  );
}  import { useState } from "react";
import { Pencil, Trash2 } from "lucide-react";
import Modal from "./ui/Modal";

export default function UserList({ users, loading, error, currentUser, onEdit, onDelete }) {
  const [editUser, setEditUser] = useState(null);
  const [updating, setUpdating] = useState(false);
  const [deleteUserModal, setDeleteUserModal] = useState(null);

  if (loading) return <p className="text-black text-center py-8">Cargando usuarios...</p>;
  if (error) return <p className="text-red-600 text-center py-8">Error: {error}</p>;
  if (!users || users.length === 0)
    return <p className="text-black text-center py-8">No hay usuarios registrados.</p>;

  return (
    <div className=" shadow-2xl border border-red-900 bg-gradient-to-br from-red-50 to-red-100 overflow-hidden">
      <table className="w-full min-w-full divide-y divide-red-200">
        {/* Encabezado */}
        <thead className="bg-gradient-to-r from-red-800 via-red-900 to-black shadow-[0_0_15px_#7f1d1d]">
          <tr>
            {["Usuario", "Email", "Celular", "Rol", "Contraseña", "Acciones"].map((header) => (
              <th
                key={header}
                className="px-6 py-4 text-left text-sm font-bold tracking-wider text-white uppercase select-none"
              >
                {header}
              </th>
            ))}
          </tr>
        </thead>

        {/* Filas */}
        <tbody className="divide-y divide-red-200">
          {users.map((user) => (
            <tr
              key={user.id_usuario}
              className={transition-all duration-300 ease-out ${
                currentUser.id_usuario === user.id_usuario
                  ? "bg-red-50 shadow-inner"
                  : "hover:bg-red-50 hover:shadow-md"
              }}
            >
              <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold text-black">
                {user.username}
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-black">{user.email}</td>
              <td className="px-6 py-4 whitespace-nowrap text-sm text-black">
                {user.celular || "—"}
              </td>
              <td className="px-6 py-4 whitespace-nowrap">
                <span
                  className={px-3 py-1 text-xs font-bold rounded-full shadow-md transition-all duration-300 ${
                    user.rol === "admin"
                      ? "bg-gradient-to-r from-red-700 to-red-900 text-white"
                      : "bg-black text-white"
                  }}
                >
                  {user.rol === "admin" ? "Administrador" : "Trabajador"}
                </span>
              </td>
              <td className="px-6 py-4 whitespace-nowrap text-sm select-none text-black">••••••••</td>
              <td className="px-6 py-4 whitespace-nowrap text-center text-sm font-medium space-x-3">
                {/* Botón editar */}
                <button
                  onClick={() => setEditUser(user)}
                  className="p-2 rounded-full bg-gradient-to-r from-red-700 to-red-900 text-white shadow-md transition-all duration-300 hover:from-red-600 hover:to-red-800 hover:ring-2 hover:ring-red-700"
                  title="Editar"
                >
                  <Pencil className="w-4 h-4" />
                </button>
                {/* Botón eliminar */}
                {currentUser.id_usuario !== user.id_usuario && (
                  <button
                    onClick={() => setDeleteUserModal(user)}
                    className="p-2 rounded-full bg-black text-white shadow-md transition-all duration-300 hover:bg-red-800 hover:ring-2 hover:ring-red-700"
                    title="Eliminar"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal editar */}
      <Modal isOpen={!!editUser} onClose={() => setEditUser(null)} title="Editar Usuario">
        {editUser && (
          <form
            onSubmit={async (e) => {
              e.preventDefault();
              setUpdating(true);
              try {
                await onEdit(editUser);
                setEditUser(null);
              } finally {
                setUpdating(false);
              }
            }}
            className="space-y-5"
          >
            {["username", "email", "celular"].map((field) => (
              <div key={field}>
                <label className="block text-sm font-medium text-black capitalize">{field}</label>
                <input
                  type={field === "email" ? "email" : "text"}
                  value={editUser[field] || ""}
                  onChange={(e) => setEditUser({ ...editUser, [field]: e.target.value })}
                  className="mt-1 block w-full rounded-lg border border-red-700 bg-white text-black shadow-sm focus:border-red-700 focus:ring-red-700 sm:text-sm p-2"
                />
              </div>
            ))}

            <button
              type="submit"
              disabled={updating}
              className={w-full flex justify-center items-center gap-2 font-medium py-2 rounded-xl transition-all shadow-lg ${
                updating
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-gradient-to-r from-red-700 to-red-900 text-white hover:from-red-600 hover:to-red-800 hover:ring-2 hover:ring-red-700"
              }}
            >
              {updating ? "Guardando..." : "Guardar Cambios"}
            </button>
          </form>
        )}
      </Modal>

      {/* Modal eliminar */}
      <Modal isOpen={!!deleteUserModal} onClose={() => setDeleteUserModal(null)} title="¿Eliminar usuario?">
        <p className="text-black text-center mb-6">
          ¿Seguro que deseas eliminar a <strong className="text-red-600">{deleteUserModal?.username}</strong>?
        </p>
        <div className="flex justify-center space-x-4">
          <button
            onClick={() => setDeleteUserModal(null)}
            className="px-4 py-2 rounded-lg bg-gray-200 hover:bg-gray-300 text-black transition-colors"
          >
            Cancelar
          </button>
          <button
            onClick={() => {
              if (deleteUserModal) onDelete(deleteUserModal.id_usuario);
              setDeleteUserModal(null);
            }}
            className="px-4 py-2 rounded-lg bg-gradient-to-r from-red-700 to-red-900 hover:from-red-600 hover:to-red-800 hover:ring-2 hover:ring-red-700 text-white shadow-md transition-all"
          >
            Eliminar
          </button>
        </div>
      </Modal>
    </div>
  );
}

